import React, { useState,useEffect  } from "react";
import axios from "../services/axiosConfig";
import "./../css/style.css";
import whiteOutline from "../img/white-outline.png";
import cohete from "../img/cohete.png";

const LoginRegister = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ username: "", email: "", password: "" });
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState(""); // "error" o "success"
  

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        // L贸gica de Inicio de Sesi贸n
        const res = await axios.post("/auth/login", {
          email: formData.email,
          password: formData.password,
        });
        localStorage.setItem("token", res.data.token);
        setMessage(res.data.message);
        setMessageType("success"); // Mensaje exitoso
      } else {
        // L贸gica de Registro
        const res = await axios.post("/auth/register", formData);
        setMessage(res.data.message);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || "Error al procesar la solicitud");
    }
  };

  // L贸gica para los botones de login y registro
  useEffect(() => {
    const loginBtn = document.querySelector("#login");
    const registerBtn = document.querySelector("#register");
    const loginForm = document.querySelector(".login-form");
    const registerForm = document.querySelector(".register-form");

    loginBtn.addEventListener('click', () => {
      loginBtn.style.backgroundColor = "#21264D";
      registerBtn.style.backgroundColor = "rgba(255, 255, 255, 0.2)";

      loginForm.style.left = "50%";
      registerForm.style.left = "-50%";

      loginForm.style.opacity = 1;
      registerForm.style.opacity = 0;

      document.querySelector(".col-1").style.borderRadius = "0 30% 20% 0";
    });

    registerBtn.addEventListener('click', () => {
      loginBtn.style.backgroundColor = "rgba(255, 255, 255, 0.2)";
      registerBtn.style.backgroundColor = "#21264D";

      loginForm.style.left = "150%";
      registerForm.style.left = "50%";

      loginForm.style.opacity = 0;
      registerForm.style.opacity = 1;

      document.querySelector(".col-1").style.borderRadius = "0 20% 30% 0";
    });

    // Cleanup event listeners when the component is unmounted
    return () => {
      loginBtn.removeEventListener('click', () => {});
      registerBtn.removeEventListener('click', () => {});
    };
  }, []);

  return (
    <div className="form-container">
      <div className="col col-1">
        <div className="image-layer">
          <img src={whiteOutline} alt="Outline" className="form-image-main" />
          <img src={cohete} alt="Cohete" className="form-image cohete" />
        </div>
        <p className="featured-words">
          <br /> Estas a punto de ingresar a una p谩gina que te ayudar谩 con tus necesidades.
        </p>
      </div>
      <div className="col col-2">
        <div className="btn-box">
          <button
            className={`btn btn-1 ${isLogin ? "active" : ""}`}
            id="login"
            onClick={() => setIsLogin(true)}
          >
            Iniciar Sesi贸n
          </button>
          <button
            className={`btn btn-2 ${!isLogin ? "active" : ""}`}
            id="register"
            onClick={() => setIsLogin(false)}
          >
            Registrarse
          </button>
        </div>

        {/* Formulario de Login */}
        <form
          className="login-form"
          style={{
            left: isLogin ? "50%" : "150%",
            opacity: isLogin ? 1 : 0,
          }}
          onSubmit={handleSubmit}
        >
          <div className="form-title">
            <span>Iniciar Sesi贸n</span>
          </div>
          <div className="form-inputs">
            <div className="input-box">
              <input
                type="email"
                name="email"
                className="input-field"
                placeholder="Correo Electr贸nico"
                onChange={handleChange}
                required
              />
              <i className="bx bx-envelope icon"></i>
            </div>
            <div className="input-box">
              <input
                type="password"
                name="password"
                className="input-field"
                placeholder="Contrase帽a"
                onChange={handleChange}
                required
              />
              <i className="bx bx-lock-alt icon"></i>
            </div>
            <div className="input-box">
              <button className="input-submit">
                <span>Iniciar Sesi贸n</span>
                <i className="bx bx-right-arrow-alt"></i>
              </button>
              {message && (
                <p className={`message ${messageType === "error" ? "error" : "success"}`}>
                  {message}
                </p>
              )}
            </div>
          </div>
        </form>

        {/* Formulario de Registro */}
        <form
          className="register-form"
          style={{
            left: isLogin ? "-50%" : "50%",
            opacity: isLogin ? 0 : 1,
          }}
          onSubmit={handleSubmit}
        >
          <div className="form-title">
            <span>Crear Cuenta</span>
          </div>
          <div className="form-inputs">
            <div className="input-box">
              <input
                type="text"
                name="username"
                className="input-field"
                placeholder="Nombre de Usuario"
                onChange={handleChange}
                required
              />
              <i className="bx bx-user icon"></i>
            </div>
            <div className="input-box">
              <input
                type="email"
                name="email"
                className="input-field"
                placeholder="Correo Electr贸nico"
                onChange={handleChange}
                required
              />
              <i className="bx bx-envelope icon"></i>
            </div>
            <div className="input-box">
              <input
                type="password"
                name="password"
                className="input-field"
                placeholder="Contrase帽a"
                onChange={handleChange}
                required
              />
              <i className="bx bx-lock-alt icon"></i>
            </div>
            <div className="input-box">
              <button className="input-submit">
                <span>Registrarse</span>
                <i className="bx bx-right-arrow-alt"></i>
              </button>
            </div>
          </div>
        </form>
      </div>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default LoginRegister;
