import React, { useState } from "react";
import axios from "../services/axiosConfig";
import "./../css/style.css";
import whiteOutline from "../img/white-outline.png";
import cohete from "../img/cohete.png";

const LoginRegister = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ username: "", email: "", password: "" });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        // Lógica de Inicio de Sesión
        const res = await axios.post("/auth/login", {
          email: formData.email,
          password: formData.password,
        });
        localStorage.setItem("token", res.data.token);
        setMessage("Inicio de sesión exitoso");
      } else {
        // Lógica de Registro
        const res = await axios.post("/auth/register", formData);
        setMessage(res.data.message);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || "Error al procesar la solicitud");
    }
  };

  return (
    <div className="form-container">
      <div className="col col-1">
        <div className="image-layer">
          <img src="/img/white-outline.png" className="form-image-main" alt="White Outline" />
          <img src="/img/cohete.png" className="form-image cohete" alt="Cohete" />
        </div>
        <p className="featured-words">
          <br />
          Estas a punto de ingresar a una página que te ayudará con tus necesidades.
        </p>
      </div>
      <div className="col col-2">
        <div className="btn-box">
          <button
            className={`btn ${isLoginActive ? "btn-1" : ""}`}
            id="login"
            onClick={handleLoginClick}
          >
            Iniciar Sesión
          </button>
          <button
            className={`btn ${!isLoginActive ? "btn-2" : ""}`}
            id="register"
            onClick={handleRegisterClick}
          >
            Registrarse
          </button>
        </div>

        <div
          className="login-form"
          style={{
            left: isLoginActive ? "50%" : "150%",
            opacity: isLoginActive ? 1 : 0,
          }}
        >
          <div className="form-title">
            <span>Iniciar Sesión</span>
          </div>
          <div className="form-inputs">
            <div className="input-box">
              <input type="text" className="input-field" placeholder="Nombre de Usuario" required />
              <i className="bx bx-user icon"></i>
            </div>
            <div className="input-box">
              <input type="password" className="input-field" placeholder="Contraseña" required />
              <i className="bx bx-lock-alt icon"></i>
            </div>
            <div className="forgot-pass">
              <a href="#">¿Olvidó su contraseña?</a>
            </div>
            <div className="input-box">
              <button className="input-submit">
                <span>Iniciar Sesión</span>
                <i className="bx bx-right-arrow-alt"></i>
              </button>
            </div>
          </div>
          <div className="social-login">
            <i className="bx bxl-google"></i>
            <i className="bx bxl-facebook"></i>
            <i className="bx bxl-twitter"></i>
            <i className="bx bxl-github"></i>
          </div>
        </div>

        <div
          className="register-form"
          style={{
            left: isLoginActive ? "-50%" : "50%",
            opacity: isLoginActive ? 0 : 1,
          }}
        >
          <div className="form-title">
            <span>Crear Cuenta</span>
          </div>
          <div className="form-inputs">
            <div className="input-box">
              <input type="text" className="input-field" placeholder="Correo Electrónico" required />
              <i className="bx bx-envelope icon"></i>
            </div>
            <div className="input-box">
              <input type="text" className="input-field" placeholder="Nombre de Usuario" required />
              <i className="bx bx-user icon"></i>
            </div>
            <div className="input-box">
              <input type="password" className="input-field" placeholder="Contraseña" required />
              <i className="bx bx-lock-alt icon"></i>
            </div>
            <div className="forgot-pass">
              <a href="#">¿Olvidó su contraseña?</a>
            </div>
            <div className="input-box">
              <button className="input-submit">
                <span>Registrarse</span>
                <i className="bx bx-right-arrow-alt"></i>
              </button>
            </div>
          </div>
          <div className="social-login">
            <i className="bx bxl-google"></i>
            <i className="bx bxl-facebook"></i>
            <i className="bx bxl-twitter"></i>
            <i className="bx bxl-github"></i>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginRegister;
