import React, { useState } from 'react';
import axios from '../services/axiosConfig';

const Register = () => {
const [formData, setFormData] = useState({ username: '', email: '', password: '' });
const [message, setMessage] = useState('');

const handleChange = (e) => {
setFormData({ ...formData, [e.target.name]: e.target.value });
};

const handleSubmit = async (e) => {
e.preventDefault();
try {
    const res = await axios.post('/auth/register', formData);
    setMessage(res.data.message);
} catch (err) {
    setMessage(err.response?.data?.error || 'Error en el registro');
}
};

return (
<div className="register-form">
      <div className="form-title">
        <span>Crear Cuenta</span>
      </div>
      <div className="form-inputs">
        <div className="input-box">
          <input type="text" className="input-field" name="username" placeholder="Nombre de Usuario" onChange={handleChange} required />
          <i className="bx bx-user icon"></i>
        </div>
        <div className="input-box">
          <input type="email" className="input-field" name="email" placeholder="Correo Electr칩nico" onChange={handleChange} required />
          <i className="bx bx-envelope icon"></i>
        </div>
        <div className="input-box">
          <input type="password" className="input-field" name="password" placeholder="Contrase침a" onChange={handleChange} required />
          <i className="bx bx-lock-alt icon"></i>
        </div>
        <div className="forgot-pass">
          <a href="#">Olvid칩 su contrase침a?</a>
        </div>
        <div className="input-box">
          <button className="input-submit" onClick={handleSubmit}>
            <span>Registrarse</span>
            <i className="bx bx-right-arrow-alt"></i>
          </button>
        </div>
      </div>
      <div className="social-login">
        <i className="bx bxl-google"></i>
        <i className="bx bxl-facebook"></i>
        <i className="bx bxl-twitter"></i>
        <i className="bx bxl-github"></i>
      </div>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Register;
