import React, { useState } from 'react';
import axios from '../services/axiosConfig';

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/auth/login', formData);
      localStorage.setItem('token', res.data.token);
      setMessage('Inicio de sesión exitoso');
    } catch (err) {
      setMessage(err.response?.data?.message || 'Error al iniciar sesión');
    }
  };

  return (
    <div className="login-form">
      <div className="form-title">
        <span>Iniciar Sesión</span>
      </div>
      <div className="form-inputs">
        <div className="input-box">
          <input type="email" className="input-field" name="email" placeholder="Correo Electrónico" onChange={handleChange} required />
          <i className="bx bx-user icon"></i>
        </div>
        <div className="input-box">
          <input type="password" className="input-field" name="password" placeholder="Contraseña" onChange={handleChange} required />
          <i className="bx bx-lock-alt icon"></i>
        </div>
        <div className="forgot-pass">
          <a href="#">Olvidó su contraseña?</a>
        </div>
        <div className="input-box">
          <button className="input-submit" onClick={handleSubmit}>
            <span>Iniciar Sesión</span>
            <i className="bx bx-right-arrow-alt"></i>
          </button>
        </div>
      </div>
      <div className="social-login">
        <i className="bx bxl-google"></i>
        <i className="bx bxl-facebook"></i>
        <i className="bx bxl-twitter"></i>
        <i className="bx bxl-github"></i>
      </div>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Login;
