import React, { useState } from 'react';
import axios from '../services/axiosConfig';

const Register = () => {
const [formData, setFormData] = useState({ username: '', email: '', password: '' });
const [message, setMessage] = useState('');

const handleChange = (e) => {
setFormData({ ...formData, [e.target.name]: e.target.value });
};

const handleSubmit = async (e) => {
e.preventDefault();
try {
    const res = await axios.post('/auth/register', formData);
    setMessage(res.data.message);
} catch (err) {
    setMessage(err.response?.data?.error || 'Error en el registro');
}
};

return (
<div>
    <h2>Registro</h2>
    <form onSubmit={handleSubmit}>
    <input name="username" placeholder="Nombre de usuario" onChange={handleChange} required />
    <input type="email" name="email" placeholder="Correo" onChange={handleChange} required />
    <input type="password" name="password" placeholder="ContraseÃ±a" onChange={handleChange} required />
    <button type="submit">Registrarse</button>
    </form>
    {message && <p>{message}</p>}
</div>
);
};

export default Register;
