import React, { useState } from "react";
import axios from "../services/axiosConfig";
import "./../css/style.css";
import whiteOutline from "../img/white-outline.png";
import cohete from "../img/cohete.png";

const LoginRegister = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ username: "", email: "", password: "" });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        // L贸gica de Inicio de Sesi贸n
        const res = await axios.post("/auth/login", {
          email: formData.email,
          password: formData.password,
        });
        localStorage.setItem("token", res.data.token);
        setMessage("Inicio de sesi贸n exitoso");
      } else {
        // L贸gica de Registro
        const res = await axios.post("/auth/register", formData);
        setMessage(res.data.message);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || "Error al procesar la solicitud");
    }
  };

  return (
    <div className="form-container">
      <div className="col col-1">
        <div className="image-layer">
          <img src={whiteOutline} alt="Outline" className="form-image-main" />
          <img src={cohete} alt="Cohete" className="form-image cohete" />
        </div>
        <p className="featured-words">
          <br /> Estas a punto de ingresar a una p谩gina que te ayudar谩 con tus necesidades.
        </p>
      </div>
      <div className="col col-2">
        <div className="btn-box">
          <button
            className={`btn btn-1 ${isLogin ? "active" : ""}`}
            onClick={() => setIsLogin(true)}
          >
            Iniciar Sesi贸n
          </button>
          <button
            className={`btn btn-2 ${!isLogin ? "active" : ""}`}
            onClick={() => setIsLogin(false)}
          >
            Registrarse
          </button>
        </div>

        {/* Formulario de Login */}
        <form
          className="login-form"
          style={{
            left: isLogin ? "50%" : "150%",
            opacity: isLogin ? 1 : 0,
          }}
          onSubmit={handleSubmit}
        >
          <div className="form-title">
            <span>Iniciar Sesi贸n</span>
          </div>
          <div className="form-inputs">
            <div className="input-box">
              <input
                type="email"
                name="email"
                className="input-field"
                placeholder="Correo Electr贸nico"
                onChange={handleChange}
                required
              />
              <i className="bx bx-envelope icon"></i>
            </div>
            <div className="input-box">
              <input
                type="password"
                name="password"
                className="input-field"
                placeholder="Contrase帽a"
                onChange={handleChange}
                required
              />
              <i className="bx bx-lock-alt icon"></i>
            </div>
            <div className="forgot-pass">
              <a href="#">Olvid贸 su contrase帽a?</a>
            </div>
            <div className="input-box">
              <button className="input-submit">
                <span>Iniciar Sesi贸n</span>
                <i className="bx bx-right-arrow-alt"></i>
              </button>
            </div>
          </div>
        </form>

        {/* Formulario de Registro */}
        <form
          className="register-form"
          style={{
            left: isLogin ? "-50%" : "50%",
            opacity: isLogin ? 0 : 1,
          }}
          onSubmit={handleSubmit}
        >
          <div className="form-title">
            <span>Crear Cuenta</span>
          </div>
          <div className="form-inputs">
            <div className="input-box">
              <input
                type="text"
                name="username"
                className="input-field"
                placeholder="Nombre de Usuario"
                onChange={handleChange}
                required
              />
              <i className="bx bx-user icon"></i>
            </div>
            <div className="input-box">
              <input
                type="email"
                name="email"
                className="input-field"
                placeholder="Correo Electr贸nico"
                onChange={handleChange}
                required
              />
              <i className="bx bx-envelope icon"></i>
            </div>
            <div className="input-box">
              <input
                type="password"
                name="password"
                className="input-field"
                placeholder="Contrase帽a"
                onChange={handleChange}
                required
              />
              <i className="bx bx-lock-alt icon"></i>
            </div>
            <div className="input-box">
              <button className="input-submit">
                <span>Registrarse</span>
                <i className="bx bx-right-arrow-alt"></i>
              </button>
            </div>
          </div>
        </form>
      </div>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default LoginRegister;
